<script>
    import GuestAuthor from "./GuestAuthor";
    export default {
        name: "GuestLogin",
        components: {
            GuestAuthor
        },
        data() {
            return {
                email: null,
                password: null,
                name: null,
            }
        },
        methods: {
            register() {

                const data = {
                    email: this.email,
                    password: this.password,
                    name: this.name
                };

                this.$auth.register({
                    data,
                    success: function () {
                        this.$router.push({ name: 'login' });
                    },
                    rememberMe: true,
                    fetchUser: true
                })


            }
        },
        mounted() {

        }
    }
</script>

